func parseTrainlineHTML(htmlStr string) (Reciept) {
        //fmt.Println(string(trainlineEmails[0].HTML))
        bodyString := strings.NewReader(htmlStr)
        token := html.NewTokenizer(bodyString)
        tokenCount := 0
        content := []string{}
        //tripDetails := []string{}
        relevantInfoPayment := false
        //relevantInfoTrip := false
    
        for {
            tt := token.Next()
            t := token.Token()
            td := t.Data
            tokenCount++
    
            if tt == html.ErrorToken {
                    break
            
            } else if(strings.Contains(td, "Payment information")){
                    relevantInfoPayment = true
                
            } else if (tt == html.TextToken && relevantInfoPayment){
                    stringContent :=  strings.TrimSpace(td)
                    if(len(stringContent) > 0){
                            content = append(content, stringContent)
                    }
            }
        }
    
        var reciept Reciept
        var taxes []Reciept_Tax
        var items []MonzoRecieptItem
        recieptItems := false;
    
        var transactionDate string;
        k := 0;
        for {      
            if(k < len(content)){

            if(content[k] == "Transaction Date:") {
                transactionDate = content[k+1]
                fmt.Println(transactionDate)
            } else if(content[k] == "Fare details") {
                recieptItems = true;
    
            } else if (content[k] == "Other costs*"){ // end of items
                recieptItems = false;
            
            } else if(recieptItems){
                
                if(strings.HasPrefix(content[k], "Booking")){
                    k++;
                }
                if(content[k] == "Outbound"){
                    k++;
                }
                if(content[k] == "Return"){
                    k++;
                }

                var thisItem MonzoRecieptItem
                thisItem.Quantity = stringToInt(strings.Split(content[k], "x")[0])
                thisItem.Description = strings.Split(content[k], "x")[1]
                thisItem.Amount = stringToPence(strings.Split(content[k + 1], "x")[1])
                k++;
                
                items = append(items, thisItem)
                
            }else if(content[k] == "Booking Fee:") { 
                var thisItem MonzoRecieptItem
                thisItem.Quantity = 1
                thisItem.Description = "Booking Fee"
                thisItem.Currency = "GBP"
                
                if(content[k + 1] == "No booking fee"){
                    thisItem.Amount = 0
                }else{
                    thisItem.Amount = stringToPence(content[k + 1])
                }
    
                items = append(items, thisItem)
            } else if(content[k] == "Total amount:"){
                reciept.Total = stringToPence(content[k + 1])
    
            } else if(strings.HasPrefix(content[k], "VAT number")){
                var VAT Reciept_Tax
                VAT.TaxNumber = strings.Split(content[k], "VAT number ")[1]
                VAT.Description = "VAT"
                VAT.Currency = "GBP"
                VAT.Amount = 0
    
                taxes = append(taxes, VAT)
                break;
            }
        }

        k++;
    
        }
    
        reciept.Items = items
        reciept.RecieptTaxes = taxes
        reciept.Currency = "GBP"
        reciept.ExternalID = ""
    
        return reciept;
}

